import React, { useEffect, useState } from 'react';
import axios from "axios";
function habilidades() {

    const [data,setData]=useState([]);

    useEffect(()=>{
        axios.get("http://localhost:3002/")
        .then(res=>{
            setData(res.data.skills)
        })
        .catch(error=>console.log(error));
    },[]);

    const firstHalf=data.slice(0,data.length/2);
    const secondHalf=data.slice(data.length/2,data.length);

  return (
    <div>
      <h4>Some skills</h4>

      <div className="container">
        <div className="row align-items-start">
        {firstHalf.map((skill,index)=>(
            <div className="col-3 col-lg-3 col-md-3 col-sm-6 " key={index}>
            <img src={skill.image} width="100%"/>
            <p>{skill.name}</p>
          </div>
        ))}
        </div>
        <div className="row align-items-start">
        {secondHalf.map((skill,index)=>(
            <div className="col-3 col-lg-3 col-md-3 col-sm-6 " key={index}>
            <img src={skill.image} width="100%"/>
            <p>{skill.name}</p>
          </div>
        ))}
        </div>
      </div>
    </div>
  );
}
export default habilidades;
